import styled, { css } from 'styled-components';
import { shade } from 'polished';

interface FormProps {
  hasError: boolean
}

export const Title = styled.h1`
  margin-top: 80px;
  max-width: 450px;
  line-height:  56px;
  font-size: 48px;
  color: '#3A3A3A';
  @media (max-width: 700px) {
    font-size: 23px;
    text-align: center
  }
`;

export const Form = styled.form<FormProps>`
  margin-top: 40px;
  max-width: 700px;
  display: flex;
  input {
    flex: 1;
    height: 70px;
    padding: 0 24px;
    border: 0;
    border-radius: 5px 0 0 5px;
    color: #3A3A3A;
    border: 2px solid #FFF;
    border-right: 0;
    ${(props) => props.hasError && css`
      border-color: #C53030;
    `}
    & ::placeholder{
      color: #A8A8B3;
    }
    @media (max-width: 700px) {
        font-size: 13px;
        text-align: center;
        padding: 0 8px;
      }
  }
  button {
    width: 210px;
    height: 70px;
    background: #04D361;
    border-radius: 0px 5px 5px 0px;
    border: 0;
    color: #FFF;
    font-weight: bold;
    transition: background-color 0.2s;
    @media (max-width: 700px) {
        width:100px
      }

    &:hover{
      background: ${shade(0.2, '#04D361')}
    }
  }
`;
export const Error = styled.span`
  display: block;
  color: #C53030;
  margin-top: 8px;
`;
export const Repositories = styled.div`
 margin-top: 80px;
  a {
    background: #FFF;
    border-radius: 5px;
    width: 100%auto;
    padding: 24px;
    display: block;
    text-decoration: none;
    display: flex;
    align-items: center;
    transition: transform 0.2s;

    @media (max-width: 700px) {
      padding: 22px;
      text-align:left;
    }

    & + a {
      margin-top: 16px;
    }

    &:hover{
      transform: translateX(10px);
    }
  img{
    width: 64px;
    height: 64px;
    border-radius: 50%;
  }
  div{
    margin: 0 16px;
    flex:1;
    strong{
      font-size: 20px;
      color: #3D3D4D;
      word-break: break-all;
      text-align:justify;

      @media (max-width: 700px) {
        font-size:16px;
      }
    }
    p{
      font-size: 18px;
      color: #A8A8B3;
      margin-top: 4px;

      @media (max-width: 700px) {
        font-size:14px;
      }
    }
  }
  svg{
      margin-left: auto;
      color: #CBCBD6;
    }
  }
`;
